import { MaterialIcons } from "@expo/vector-icons";
import { StatusBar } from 'expo-status-bar';
import uuidv1 from 'uuid/v1';
import React, { Component } from 'react'
import { StyleSheet, Text, View,ScrollView, KeyboardAvoidingView, Platform, TextInput, TouchableOpacity, Keyboard, FlatList  } from 'react-native';
import BouncyCheckbox from "react-native-bouncy-checkbox";
import AsyncStorage from '@react-native-async-storage/async-storage';
import _ from 'lodash'
import { Form, Item, Input, Button, Text as NBText } from 'native-base'

export class  HomeTodo extends Component {


	state = {
		todos: {},
		isDataReady: false,
		filter: 'Todo',
		text: ''
	}

	componentDidMount = () => {
		this.loadTodos()
	}

	onChangeText = text => {
		this.setState({ task: text })
	}

	loadTodos = async () => {
		try {
			const getTodos = await AsyncStorage.getItem('todos')
			const parsedTodos = JSON.parse(getTodos)
			this.setState({ isDataReady: true, todos: parsedTodos || {} })
		} catch (err) {
			alert('Application Error. Cannot load data.')
		}
	}

	addTodo = newTask => {
		//debugger
		const newTodoItem = this.state.task

		if (newTodoItem !== '') {
			this.setState(prevState => {
				const ID = uuidv1()
				const newToDoObject = {
					[ID]: {
						id: ID,
						isChecked: false,
						textValue: newTodoItem,
						createdAt: Date.now()
					}
				}
				const newState = {
					...prevState,
					todos: {
						...prevState.todos,
						...newToDoObject
					}
				}
				this.saveTodos(newState.todos)
				return { ...newState }
			})
		}
	}

	deleteTodo = id => {
		this.setState(prevState => {
			const todos = prevState.todos
			delete todos[id]
			const newState = {
				...prevState,
				...todos
			}
			this.saveTodos(newState.todos)
			return { ...newState }
		})
	}

	inCompleteTodo = id => {
		this.setState(prevState => {
			const newState = {
				todos: {
					...prevState.todos,
					[id]: {
						...prevState.todos[id],
						isChecked: false
					}
				}
			}
			this.saveTodos(newState.todos)
			return { ...newState }
		})
	}

	completeTodo = id => {
		this.setState(prevState => {
			const newState = {
				...prevState,
				todos: {
					...prevState.todos,
					[id]: {
						...prevState.todos[id],
						isChecked: true
					}
				}
			}
			this.saveTodos(newState.todos)
			return { ...newState }
		})
	}


	saveTodos = newToDos => {
		const saveTodos = AsyncStorage.setItem('todos', JSON.stringify(newToDos))
	}

	onPressFab = () => {
		this.props.navigation.navigate('AddTask', {
			saveItem: this.addTodo
		})
	}

	filteredItems = () => {
		// if (this.state.filter === 'Todo') {
		// 	return _.values(this.state.todos).filter(i => {
		// 		return i
		// 	})
		// }
		// if (this.state.filter === 'Todo') {
		// 	return _.values(this.state.todos).filter(i => {
		// 		return i
		// 	})
		// }
		return this.state.todos
	}

	toggleItem = id => {
		if (this.isChecked) {
			this.inCompleteTodo(id)
		} else {
			this.completeTodo(id)
		}
	}

  render() {
	// const { isDataReady, filter } = this.state

	// if (!isDataReady) {
	// 	return <AppLoading />
	// }
		return (
		<View style={styles.container}>
		<View style={styles.tasksWrapper}>
			<Text style={styles.sectionTitle}>Today's tasks</Text>

		</View>

		<FlatList
					data={_.values(this.filteredItems())}
					contentContainerStyle={styles.content}
					renderItem={row => {
						return (

							<View style={styles.item}>
							<View style={styles.itemLeft}>
							 	<View > 
							 	<BouncyCheckbox
							 		size={25}
							 		fillColor="#188078"
									iconStyle={{ borderColor: "#188078" }}
									onPress={() => this.toggleItem(row.item.id)}
									isChecked={row.item.isChecked ? false : false}
							 	/>
							 	</View>
							</View>
							<Text style={styles.text}>{row.item.textValue}</Text>
							<TouchableOpacity onPress={() => this.deleteTodo(row.item.id)}>
							 		<MaterialIcons name="delete" size={25} color="#188078" />
							</TouchableOpacity>
							</View>
							// <Item
							// 	isChecked={row.item.isChecked}
							// 	textValue={row.item.textValue}
							// 	id={row.item.id}
							// 	deleteTodo={this.deleteTodo}
							// 	completeTodo={this.completeTodo}
							// 	inCompleteTodo={this.inCompleteTodo}
							// />
						)
					}}
					keyExtractor={item => item.id}
		/>

		{/* <ScrollView>
		<View style={styles.items}>
			{/* {
				todo_list.map((item, index) => {
				return (
					<View style={styles.item}>
					<View style={styles.itemLeft}>
						<View > 
						<BouncyCheckbox
							size={25}
							fillColor="#188078"
							iconStyle={{ borderColor: "#188078" }}
						/>
						</View>
					</View>
					<Text style={styles.text}>{item.task}</Text>
					<TouchableOpacity key={index} onPress={() => completeTask(item.id)}>
							<MaterialIcons name="delete" size={25} color="#188078" />
					</TouchableOpacity>
					</View>
				) 
			})
			}
		</View>
		</ScrollView> */}

			{/* <FlatList
					data={todo_list}
					keyExtractor={(item) => item.id}
					renderItem={({item, index}) => {
					return (

						<View style={styles.item}>
						<View style={styles.itemLeft}>
							<View > 
							<BouncyCheckbox
								size={25}
								fillColor="#188078"
								iconStyle={{ borderColor: "#188078" }}
							/>
							</View>
						</View>
						<Text style={styles.text}>{item.task}</Text>
						<TouchableOpacity key={index} onPress={() => completeTask(item.id)}>
								<MaterialIcons name="delete" size={25} color="#188078" />
						</TouchableOpacity>
						</View>
						// <>
						// <Card>
						// <Card.Title
						// 	title={`Task#${item.id}`}
						// 	left={(props) => <Icon name="tasks" size={24} color="black" />}
						// 	right={(props) => <ButtonIcon iconName="close" color="red" onPress={() => handleDeleteTodo(item.id)} />}
						// />
						// <Card.Content>
						// 	<Paragraph>{item.task}</Paragraph>
						// </Card.Content>
						// </Card>
						// <Spacer />
						// </>
					);
					}}
			/> */}

		<KeyboardAvoidingView 
			behavior={Platform.OS === "ios" ? "padding" : "height"}
			style={styles.writeTasksWrapper}
		>
			<TextInput style={styles.input} placeholder={"Write a task"} value={this.state.task} onChangeText={this.onChangeText} />

			<TouchableOpacity onPress={this.addTodo}>
			<View style={styles.addWrapper}>
				<Text style={styles.addText}>+</Text>

			</View>
			</TouchableOpacity>

		</KeyboardAvoidingView>
		</View>
	);
	}
}

const styles = StyleSheet.create({
	item: {
		margin: "auto",
		marginLeft: 10,
		maxHeight: "80%",
		width: "95%",
		maxWidth: "95%",
        backgroundColor: '#FFF',
        padding: 15,
        borderRadius: 20,
        flexDirection: 'row',
        alignItems: "center",
