import { MaterialIcons } from "@expo/vector-icons";
import { StatusBar } from 'expo-status-bar';
import React, {useState} from 'react';
import { StyleSheet, Text, View,ScrollView, KeyboardAvoidingView, Platform, TextInput, TouchableOpacity, Keyboard } from 'react-native';
import BouncyCheckbox from "react-native-bouncy-checkbox";

export default function App() {


  const [task, setTask] = useState();

  const [taskItems, setTaskItems] = useState([]);

  let myHeight = 50;

  const handleAddTask = () => {
    Keyboard.dismiss();
	setTaskItems([...taskItems, task])
	setSelection(true);
    setTask(null);
  }

  const completeTask = (index) => {
    let itemsCopy = [...taskItems];
    itemsCopy.splice(index, 1);
    setTaskItems(itemsCopy);
  }

  const [isSelected, setSelection] = useState(false);


  return (
    <View style={styles.container}>
      <View style={styles.tasksWrapper}>
        <Text style={styles.sectionTitle}>Today's tasks</Text>

      </View>

	  <ScrollView>
      <View style={styles.items}>
        {
          taskItems.map((item, index) => {
            return (
				<View style={styles.item}>
				<View style={styles.itemLeft}>
					<View > 
					<BouncyCheckbox
						size={25}
						fillColor="#188078"
						iconStyle={{ borderColor: "#188078" }}
					/>
					</View>
				</View>
				<Text style={styles.text}>{item}</Text>
				<TouchableOpacity key={index} onPress={() => completeTask(index)}>
						<MaterialIcons name="delete" size={25} color="#188078" />
				</TouchableOpacity>
				</View>
            ) 
          })
        }
      </View>
	  </ScrollView>

      <KeyboardAvoidingView 
        behavior={Platform.OS === "ios" ? "padding" : "height"}
        style={styles.writeTasksWrapper}
      >
        <TextInput style={styles.input} placeholder={"Write a task"} value={task} onChangeText={text => setTask(text)} />

        <TouchableOpacity onPress={() => handleAddTask()}>
          <View style={styles.addWrapper}>
            <Text style={styles.addText}>+</Text>

          </View>
        </TouchableOpacity>

      </KeyboardAvoidingView>
    </View>
  );
}
