import { MaterialIcons } from "@expo/vector-icons";
import { StatusBar } from 'expo-status-bar';
import React, {useState} from 'react';
import { StyleSheet, CheckBox, Text, View, KeyboardAvoidingView, Platform, TextInput, TouchableOpacity, Keyboard } from 'react-native';


export default function App() {

  const [task, setTask] = useState();

  const [taskItems, setTaskItems] = useState([]);

  const handleAddTask = () => {
    Keyboard.dismiss();
    setTaskItems([...taskItems, task])
    setTask(null);
  }

  const completeTask = (index) => {
    let itemsCopy = [...taskItems];
    itemsCopy.splice(index, 1);
    setTaskItems(itemsCopy);
  }

  const [isSelected, setSelection] = useState(false);

  return (
    <View style={styles.container}>
      <View style={styles.tasksWrapper}>
        <Text style={styles.sectionTitle}>Today's tasks</Text>

      </View>

      <View style={styles.items}>
        {
          taskItems.map((item, index) => {
            return (
				<View style={styles.item}>
				<View style={styles.itemLeft}>
					<View > 
						<CheckBox
							value={isSelected}
							onValueChange={setSelection}
							style={styles.checkbox}
						/> 
					</View>
					<Text style={styles.text}>{item}</Text>
				</View>
				<TouchableOpacity key={index} onPress={() => completeTask(index)}>
						<MaterialIcons name="delete" height={50} sizr={24} color="#ce4257" />
				</TouchableOpacity>
				{/* <View style={styles.circular}></View> */}
				</View>
            ) 
          })
        }
      </View>

      <KeyboardAvoidingView 
        behavior={Platform.OS === "ios" ? "padding" : "height"}
        style={styles.writeTasksWrapper}
      >
        <TextInput style={styles.input} placeholder={"Write a task"} value={task} onChangeText={text => setTask(text)} />

        <TouchableOpacity onPress={() => handleAddTask()}>
          <View style={styles.addWrapper}>
            <Text style={styles.addText}>+</Text>

          </View>
        </TouchableOpacity>

      </KeyboardAvoidingView>

    </View>
  );
}

const styles = StyleSheet.create({
	item: {
        backgroundColor: '#FFF',
        padding: 15,
        borderRadius: 10,
        flexDirection: 'row',
        alignItems: "center",
        justifyContent: "space-between",
        marginBottom: 20
    },
    itemLeft: {
        flexDirection: 'row',
        alignItems: "center",
        flexWrap: 'wrap'
    },
    square: {
        width: 24,
        height: 24,
        backgroundColor: '#55BCF6',
        opacity: 0.4,
        borderRadius: 5,
        marginRight: 15
    },
    text: {
		margin: 10,
        maxWidth: '65%'
    },
    circular: {
        width: 12,
        height: 12,
        borderColor: '#55BCF6',
        borderWidth: 2,
        borderRadius: 5,

    },
  container: {
    flex: 1,
    backgroundColor: '#E8EAED',

  },
  tasksWrapper: {
    paddingTop: 80,
    paddingHorizontal: 20

  },
  sectionTitle: {
    fontSize: 24,
    fontWeight: 'bold'
  },
  items: {
    marginTop: 30,
  },
  writeTasksWrapper : {
    position: "absolute",
    bottom: 60,
	with: "100%",
    flexDirection: "row",
    justifyContent: "space-around",
    alignItems: "center"
