import { ADD_TODO, DELETE_TODO } from "./actionTypes";

let nextTodoId = 0;

export const addTask = task => ({
  type: ADD_TODO,
  payload: {
    id: ++nextTodoId,
    task
  }
});

export const deleteTask = id => ({
  type: DELETE_TODO,
  payload: {
    id
  }
});
